import plaid from './plaid';
import welcome from './welcome';

const modules = {
 // plaid,
  welcome
};

function pluck(prop) {
  return Object.keys(modules).reduce((acc, cur) => {
    acc[cur] = modules[cur][prop];
    return acc;
  }, {});  
}

const reducers = pluck('reducer');
const components = pluck('components');
const actions =  pluck('actions');

module.exports = {
  modules,
  reducers,
  components,
  actions
}