import plaid from './plaid';
import welcome from './welcome';

const components = {
  plaid,
  welcome
};

function pluck(prop) {
  return Object.keys(components).reduce((acc, cur) => {
    console.log(cur + '_' + prop)
    acc[cur + '_' + prop] = components[cur][prop];
    return acc;
  }, {});  
}

const reducers = pluck('reducer');

module.exports = {
  components,
  reducers
}