import React, { Component } from 'react';
import { Text, View, StyleSheet } from 'react-native';

const screens = {
  welcome:  require('@components/welcome').component,
  plaid: require('@components/plaid').component,
  login: require('@components/login').component,
  create_account: require('@components/create_account').component
};

class TopNav extends React.Component {
  render() {
    const obj = {
      screen : screens[this.props.top_screen]
    }
    return <obj.screen top_navigator={this} />;
  }

  show(screen) {
    this.props.show(screen);
  }

  back() {
    this.show(this.props.last_screen);
  }
}
export default TopNav;
