
export default (state = {top_screen: 'welcome', last_screen: null}, action) => {
  if (action.type === 'top_nav_screen') {
    console.log('Changing top screen', action.screen);
    return Object.assign({}, state, {
      last_screen: state.top_screen,
      top_screen: action.screen
    });
  }
  return state;
};