import React, { PropTypes, Component } from 'react';
import { Text, StyleSheet, View, Image } from 'react-native';

class Welcome extends Component {

  render() {
    
    return <View style={styles.container}>
        <Text             
          onPress={() => this.props.top_navigator.show('plaid')}
          style={styles.message}>Plaid</Text>

        <Text             
          onPress={() => this.props.top_navigator.show('create_account')}
          style={styles.message}>Create Account</Text>
        <Text             
          onPress={() => this.props.top_navigator.show('login')}
          style={styles.message}>login</Text>
      </View>
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#ccc'
  },
  message: {
    paddingTop:50
  }
});


export default Welcome;
