
var foo =0 

export default (state = {show: true}, action) => {

  if (action.type === 'start_plaid_flow')
    return Object.assign({}, state, {
      show: false
    });
  
  else if (action.type === 'plaid_exit') {
    return Object.assign({}, state, {
      show: true
    })
  }
  return state;
}